import React, { useEffect, useState } from "react";

import { useFormik } from "formik";

import styles from "./RegisterDriver.module.css";
import { TextField, Button } from "@material-ui/core";

const RegisterDriver = () => {
  const dispatch = useDispatch();

  const formik = useFormik({
    initialValues: { email: "xxss@gmail.com", password: "0592413118" },

    validationSchema: Yup.object({
      email: Yup.string()
        .required("Sorry the email is required")
        .email("This is an invalid email"),

      password: Yup.string().required("Sorry the password is required"),
    }),

    onSubmit: (values) => {
      setLoading(true);
      handleSubmit(values);
    },
  });

  return (
    <div className={styles.container}>
      <form className={styles.form}>
        <div className={styles.Formtitle}>Sign UP NEW Driver</div>
        <div className={styles.formGroup}>
          <TextField
            style={{ width: "50%", fontSize: "70px" }}
            name="email"
            label="Enter your email"
            variant="outlined"
          />
          <TextField
            style={{ width: "50%", fontSize: "70px" }}
            name="name"
            label="Enter your name"
            variant="outlined"
          />
          <TextField
            style={{ width: "50%", fontSize: "60px" }}
            name="password"
            label="Enter your Password"
            variant="outlined"
          />
          <Button
            variant="contained"
            color="primary"
            type="submit"
            size="small"
          >
            Register
          </Button>
        </div>
      </form>
    </div>
  );
};

export default RegisterDriver;
