import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useFormik } from "formik";

import styles from "./RegisterDriver.module.css";
import { TextField, Button } from "@material-ui/core";
import SaveIcon from "@material-ui/icons/Save";
import { registerDriver } from "../../../store/user";

const RegisterDriver = (props) => {
  const dispatch = useDispatch();
  const notifications = useSelector((state) => state.entities.notifications);

  const formik = useFormik({
    initialValues: {
      email: "xxss@gmail.com",
      name: "mohammed Almallahi",
      password: "0592413118",
    },

    onSubmit: (values) => {
      dispatch(registerDriver(values));
    },
  });

  useEffect(() => {
    if (notifications && notifications.success) {
      props.history.push("/searchDriver");
    }
  }, [notifications]);

  return (
    <div className={styles.container}>
      <form className={styles.form} onSubmit={formik.handleSubmit}>
        <div className={styles.Formtitle}>Sign UP NEW Driver</div>
        <div className={styles.formGroup}>
          <TextField
            className={styles.TextField}
            name="email"
            label="Enter your email"
            variant="outlined"
            inputProps={{ style: { fontSize: 20 } }}
            {...formik.getFieldProps("email")}
          />
          <TextField
            className={styles.TextField}
            name="name"
            label="Enter your name"
            variant="outlined"
            inputProps={{ style: { fontSize: 20 } }}
            {...formik.getFieldProps("name")}
          />
          <TextField
            className={styles.TextField}
            name="password"
            label="Enter your Password"
            variant="outlined"
            inputProps={{ style: { fontSize: 20 } }}
            {...formik.getFieldProps("password")}
          />
          <Button
            variant="contained"
            color="primary"
            size="large"
            className={classes.button}
            startIcon={<SaveIcon />}
          >
            Save
          </Button>
        </div>
      </form>
    </div>
  );
};

export default RegisterDriver;
